Question.TranslatedText:
                    General  - Treatment week:  - <div id="occurrence"></div>
<script src="includes/jmesa/jquery.min.js"></script>
<script>
    $.noConflict();
    jQuery(document).ready(function($) {
        var fieldOccurrence = $("#occurrence").parent().parent().find("input");
        fieldOccurrence.attr("readonly", true);
        var occurrence = $(".tablebox_center").find("tbody:first").children("tr:nth-child(2)").children("td:nth-child(2)").text();
        occurrence = $.trim(occurrence);
        var posBracket = occurrence.indexOf("(");
        occurrence = occurrence.substring(posBracket + 1, posBracket + 12);
        if(fieldOccurrence.val()!=occurrence){
            fieldOccurrence.val(occurrence);
            fieldOccurrence.change();
        }
    });
</script>

                    DM = Demographics  - Gender:  - <div id="sex"></div>
<script src="includes/jmesa/jquery.min.js"></script>
<script>
    $.noConflict();
    jQuery(document).ready(function($) {
        var fieldSex = $("#sex").parent().parent().find("input");
        fieldSex.attr("readonly", true);
        var sex = $(".tablebox_center").find("tbody:first").children("tr:nth-child(1)").children("td:nth-child(4)").text();
        sex = $.trim(sex);

        if(fieldSex.val() != sex){
            fieldSex.val(sex);
            fieldSex.change();
        }
    });
</script>

