                    AE = Adverse Event  - Evaluation of the AE report from the:  - <div id="eventdate"></div>
<script src="includes/jmesa/jquery.min.js"></script>
<script>
    $.noConflict();
    jQuery(document).ready(function($) {
        var fieldEventdate = $("#eventdate").parent().parent().find("input");
        fieldEventdate.attr("readonly", true);
        var eventdate = $(".tablebox_center").find("tbody:first").children("tr:nth-child(1)").children("td:nth-child(2)").text();
        eventdate = $.trim(eventdate);
        var startIndex = eventdate.indexOf("(");
        var endIndex = eventdate.indexOf(")");
        eventdate = eventdate.substring(startIndex + 1, endIndex);

        if(fieldEventdate.val() != eventdate){
            fieldEventdate.val(eventdate);
            fieldEventdate.change();
        }
    });
</script>

